import React from 'react'
const tasks = [
  {
    id: 1,
    text: 'Doctors Appointment',
    day: 'Jun 5th at 3:30pm',
    reminder: true,
  },
  {
    id: 2,
    text: 'Meeting at Office',
    day: 'Jun 6th at 9:30am',
    reminder: true,
  },
  {
    id: 3,
    text: 'Going to GYM',
    day: 'Jun 7th at 6:30pm',
    reminder: false,
  },
]
const Tasks = () => {
  return (
    <>
      {tasks.map((task) => (
          <div>
        <h3>{task.text}</h3>

        </div>
      ))}
    </>
  )
}

export default Tasks
